@import url('https://fonts.googleapis.com/css2?family=Roboto:ital,wght@0,100;0,300;0,400;0,500;0,700;0,900;1,100;1,300;1,400;1,500;1,700;1,900&display=swap');

@font-face {
    font-family: 'fonteslowdex';
    src: url('../fonts/Slowdex.otf') format('opentype');
}
@font-face {
    font-family: 'fonteRIOT';
    src: url('../font/ProtestRiot-Regular.ttf') format('truetype');
}

@font-face {
    font-family: 'fontejust';
    src: url('../font/Just\ Believe.otf') format('opentype');
}

*{
    margin: 0%;
    padding: 0%;
    font-family: 'Roboto', sans-serif;
}
body{
    display: flex;
    flex-direction: column;
    width: 100%;
    height: 100vh;
    background-color: var(--branco);
    
}
.box{
    display: flex;
    flex-direction: column;
    width: 100%;
    height: 100vh;
    justify-content: center;
    align-items: center;

    margin-top: 50px;
    width: 100%;

    
}
.box {
    text-align: center;
  }
  
  .box {
    text-align: center;
    overflow: hidden; /* Ocultar qualquer conteúdo que ultrapasse a largura */
  }
  
  h1 {
    font-family: 'fonteslowdex';
    font-weight: bold;
    font-size: 10rem;
    display: inline-block;
    margin: 0;

    color: var(--amarelo);
  }

  span {
    font-family: 'fonteslowdex';
    font-weight: bold;
    font-size: 10rem;
    display: inline-block;
    margin: 0;
    color: var(--azul);
  }
  
  .cash {
    transform: translateX(-100%);
    animation: slideFromLeft 2s forwards;
  }
  
  .flow {
    transform: translateX(100%);
    animation: slideFromRight 2s forwards;
  }
  
  @keyframes slideFromLeft {
    from {
      transform: translateX(100%);
    }
    to {
      transform: translateX(0);
    }
  }
  
  @keyframes slideFromRight {
    from {
      transform: translateX(-60%);
    }
    to {
      transform: translateX(0);
    }
  }
  
  


#conteiners{
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    padding: 5px;
}

.conteiner{
    display: flex;
    justify-content: start;
    align-items: center;
    text-align: center;
    background-color: var(--branco);
    border-radius: 5px;
    width: 630px;
    min-height: 85vh;
}

.lista{
    width: 95%;
    height: 100%;
    overflow: auto;
}

.lista::-webkit-scrollbar{
    width: 2px;
    height: 2px;
}
.lista::-webkit-scrollbar-thumb {
    background-color: black;
}
.lista::-webkit-scrollbar-track {
    background-color: var(--cinza);
}

.left{
    display: flex;
    flex-direction: column;
}

#bottons-left{
    width: 80%;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-evenly;
    margin-bottom: 19px;
}
.topo{
    width: 100px;
}




.right{
    display: flex;
    flex-direction: column;
    margin-left: 10px;
}

#txt-left{
    padding: 10px;
    font-size: 20px;
    font-weight: bolder;
    margin-bottom: 5px;
}
#txt-right{
    padding: 10px;
    font-size: 20px;
    font-weight: bolder;
}
#campo-right{
    display: flex;
    flex-direction: row;
}
#busca{
    margin: 10px;
    width: 30vw;
    height: 30px;border: 2px solid var(--azul);
    border-radius: 5px;
    padding-left: 6px;
